#!/usr/bin/env python3 """ Script to update the first two columns of a FAM file with sequential IDs from 1 to 2400 """
def update_fam_file(input_file, output_file): """Update first two columns with sequential numbers 1-2400""" with open(input_file, 'r') as infile, open(output_file, 'w') as outfile: for i, line in enumerate(infile, 1): # Split the line into columns columns = line.strip().split()
# Update first two columns with sequential number columns[0] = str(i) columns[1] = str(i)
# Write the updated line outfile.write(' '.join(columns) + '\n')
if __name__ == "__main__": input_file = "/Users/davidenoma/PycharmProjects/wkas_pipeline/genotype_data/test_geno.fam" output_file = "/Users/davidenoma/PycharmProjects/wkas_pipeline/genotype_data/test_geno_updated.fam"
update_fam_file(input_file, output_file) print(f"Updated FAM file saved as: {output_file}")
